pragma solidity ^0.5.0;

contract RegistrarRole {

    mapping (address => bool) private registrars;

    modifier onlyRegistrars() {
        require(isRegistrar(msg.sender), "Unknown registrar");
        _;
    }

    constructor() public {
        registrars[msg.sender] = true;
    }

    function addRegistrar(address registrar) public onlyRegistrars {
        registrars[registrar] = true;
    }

    function isRegistrar(address registrar) private view returns (bool) {
        return registrars[registrar];
    }
}
