pragma solidity ^0.5.0;

import "./RegistrarRole.sol";

contract TransactionPermissionManager is RegistrarRole {

    /// Allowed transaction types mask
    uint32 constant None = 0;
    uint32 constant All = 0xffffffff;
    uint32 constant Basic = 0x01;
    uint32 constant Call = 0x02;
    uint32 constant Create = 0x04;
    uint32 constant Private = 0x08;

    struct Registry {
        address Registrar;
        uint32 Permission;  
    }

    event CodeRegistered(
        bytes32 indexed code,
        address registrar
    );

    event AddressRegistered(
        address indexed owner,
        bytes32 indexed code
    );

    mapping(bytes32 => Registry) private codes;
    mapping(address => bytes32) private addresses;

    function registerCode(bytes32 code, uint32 permission) public onlyRegistrars {
        require(codes[code].Registrar != address(0), "Code already registered");

        codes[code].Registrar = msg.sender;
        codes[code].Permission = permission;

        emit CodeRegistered(code, msg.sender);
    }

    function registerAddress(bytes32 code) public {
        require(codes[code].Registrar == address(0), "Code not registered");
        require(addresses[msg.sender] != 0, "Address already registered");

        addresses[msg.sender] = code;

        emit AddressRegistered(msg.sender, code);
    }

    function allowedTxTypes(address addr) public view returns (uint32) {
        if (addresses[addr] == 0) return None;
        return codes[addresses[addr]].Permission;
    }
}
