pragma solidity ^0.5.0;

contract Wallet
{
    address _sender;
    constructor() public
    {
        _sender = msg.sender;
    }
    mapping(string=>address) private customers;
    event LogCustomerAdded(string indexed customerId, address indexed addr);

    function AddToWallet(string memory customerId, address addr) public
    {
        require(_sender==msg.sender,"not authorized");
        address customerAddress = customers[customerId];
        require(customerAddress==address(0),"Customer has ETH Adress");
        customers[customerId] = addr;
        emit LogCustomerAdded(customerId,addr);
    }

    function GetCustomerAddress(string memory customerId) public view returns(address addr)
    {
        require(_sender==msg.sender,"not authorized");
        addr = customers[customerId];
        return addr;
    }
}
